mapper.select:
select 

from SYS_DIC
where DICTIONARY_NO = #{dictionaryNo,jdbcType=VARCHAR}
  and DICTIONARY_CATEGORY_NO = #{dictionaryCategoryNo,jdbcType=VARCHAR}

mapper.delete:
delete from SYS_DIC
where DICTIONARY_NO = #{dictionaryNo,jdbcType=VARCHAR}
  and DICTIONARY_CATEGORY_NO = #{dictionaryCategoryNo,jdbcType=VARCHAR}
